import{_ as e,c as o,o as t,a as l}from"./app.4f83a77f.js";const S=JSON.parse('{"title":"\u524D\u7AEF\u6846\u67B6\u4E0B\u7684\u6218\u4E89","description":"","frontmatter":{"updateTime":"2019/01/28","title":"\u524D\u7AEF\u6846\u67B6\u4E0B\u7684\u6218\u4E89"},"headers":[],"relativePath":"ESSAY/beyondFrameworks.md","lastUpdated":1660300084000}'),r={name:"ESSAY/beyondFrameworks.md"},s=l("<p>\u524D\u7AEF\u6846\u67B6\u3001\u5DE5\u5177\u8D8A\u6765\u8D8A\u591A\u3002\u501F\u52A9\u8FD9\u4E9B\uFF0C\u6709\u7684\u5982\u9C7C\u5F97\u6C34\uFF0C\u5927\u5C55\u8EAB\u624B\uFF1B\u6709\u7684\u4E5F\u6DF1\u611F\u56F0\u60D1\uFF0C\u75B2\u4E8E\u5B66\u4E60\uFF0C\u4E5F\u4E0D\u77E5\u5B66\u4E60\u8FD9\u4E48\u591A\u4E1C\u897F\u7684\u51FA\u8DEF\u5728\u54EA\u3002 \u4E5F\u542C\u5230\u8FC7\u8BB8\u591A\u6709\u8DA3\u7684\u6280\u672F\u8BBA\u70B9\u3002</p><blockquote><p><strong>\u4F7F\u7528\u524D\u540E\u7AEF\u5206\u79BB\u6846\u67B6\u7684\u4EBA/\u9879\u76EE/\u516C\u53F8\u6280\u672F\u6BD4\u8F83\u5148\u8FDB/\u725B\uFF1Fxx\u6846\u67B6\u5BFC\u81F4\u4E86oo\u95EE\u9898\uFF1Fa\u6846\u67B6\u662F\u5783\u573E\uFF0C\u4E0D\u5982b\u6846\u67B6\uFF1F</strong></p></blockquote><p>\u4E0D\u53EF\u5426\u8BA4\u7684\u662F\uFF0C\u524D\u540E\u7AEF\u5206\u79BB\u7684\u5F00\u53D1\u601D\u60F3\u548C\u6A21\u5F0F\u662F\u66F4\u5148\u8FDB\u7684\u3002 \u800C\u6846\u67B6\u4E0D\u8FC7\u662F\u4E2A\u5DE5\u5177\u800C\u5DF2\uFF0C\u5173\u952E\u8FD8\u662F\u8981\u770B\u4F7F\u7528\u7684\u4EBA\u3002 \u8BB0\u5F97\u7B2C\u4E00\u6B21\u63A5\u624B\u4E00\u4E2A\u524D\u540E\u7AEF\u5206\u79BB\u5F00\u53D1\u6A21\u5F0F\u7684\u5546\u4E1A\u9879\u76EE\u65F6\uFF0C\u4E00\u6253\u5F00\u524D\u4EBA\u7684\u4EE3\u7801\uFF0C\u51E0\u4E4E\u628A\u6211\u60CA\u5230\u4E0B\u5DF4\u63A5\u4E0D\u4E0A\u3002\u968F\u4FBF\u4E3E\u51E0\u70B9\uFF1A</p><ul><li>css \u6837\u5F0F\uFF0C\u76F4\u63A5\u4F7F\u7528 inline \u7684\u65B9\u5F0F\u5199\u5728 html \u7ED3\u6784\u4E2D\uFF0C\u524D\u540E\u7AEF\u5728\u5F00\u53D1\u6A21\u5F0F\u4E0A\u662F\u5206\u79BB\u4E86\uFF0C\u800C\u6837\u5F0F\u548C\u7ED3\u6784\u3001\u884C\u4E3A\u5374\u6DF7\u5408\u5728\u4E00\u8D77</li><li>\u7EC4\u4EF6\u95F4\u7684\u53D8\u91CF\u4F20\u9012\uFF0C\u5404\u5904 <code>$refs</code> \u9F50\u98DE\uFF0C\u4EC0\u4E48 View-Model\u3001\u54CD\u5E94\u5F0F\uFF0C\u60F3\u627E\u4E0B\u6570\u636E\u662F\u54EA\u91CC\u88AB\u6539\u53D8\u7684\u90FD\u56F0\u96BE\uFF0C\u66F4\u4E0D\u7528\u8BF4\u653E\u7740 <code>vuex</code> \u4E0D\u7528\u4E86</li><li>\u5F00\u53D1\u4EBA\u5458\u5206\u4E86\u597D\u51E0\u6279\uFF0C\u4EE3\u7801\u98CE\u683C\u4E5F\u5404\u5F02\uFF0C\u4F1A\u53D1\u73B0\u540C\u4E00\u79CD\u6548\u679C\uFF0C\u6709\u6570\u79CD\u4E0D\u540C\u7684\u5B9E\u73B0\u65B9\u5F0F\uFF0C\u66F4\u6709\u7684\u4E3A\u4E86\u6539\u4E0D\u540C\u7684\u5B57\uFF0C\u5C31\u628A\u4E00\u4E2A\u7EC4\u4EF6\u590D\u5236\u51FA\u5341\u591A\u4EFD\u6765\u4FEE\u6539</li><li>UI\u7EC4\u4EF6\u5DF2\u6709\u5B8C\u5584\u7684\u529F\u80FD\uFF0C\u8FD8\u7528\u6781\u9EBB\u70E6\u7684\u539F\u751F\u65B9\u6CD5\u6765\u5B9E\u73B0\uFF0C\u5200\u8015\u706B\u79CD\uFF0C\u7801\u98CE\u6DF3\u6734\uFF0C\u5374\u660E\u663E\u4E0D\u662F\u5728\u70AB\u6280\u800C\u53EA\u662F\u56E0\u4E3A\u6CA1\u4ED4\u7EC6\u770B\u7EC4\u4EF6\u7684\u5B98\u65B9\u6587\u6863</li><li>\u6211\u6765\u5230\u9879\u76EE\u7EC4\u65F6\uFF0C\u9879\u76EE\u7ECF\u7406/\u540E\u7AEF/IOS\u5F00\u53D1\u51E0\u79CD\u4EBA\u5458\u80FD\u540C\u65F6\u517C\u524D\u7AEF\u5DE5\u4F5C\u7684\uFF0C\u53EF\u4EE5\u8111\u8865\u4E00\u4E0B\u662F\u4E00\u79CD\u4EC0\u4E48\u60C5\u51B5</li><li>\u4E0D\u52A0\u601D\u7D22\u5730\u4F7F\u7528 <code>if-else</code> <code>for</code>\uFF0C\u4EE3\u7801\u5197\u957F\u800C\u6613\u8BFB\u6027\u5DEE\uFF0C\u5176\u5B9E\u6839\u672C\u5C31\u4E0D\u7528\u5224\u65AD\u6216\u8005\u624B\u52A8\u5B9E\u73B0\u5FAA\u73AF\u7684</li></ul><p>\u8FD9\u6837\u7684\u60C5\u51B5\u4E5F\u662F\u6BD4\u8F83\u5E38\u89C1\u4E86\u3002\u4F46\u66B4\u9732\u51FA\u7684\u95EE\u9898\uFF0C\u548C\u4F7F\u7528\u4E86\u4EC0\u4E48\u5F00\u53D1\u6A21\u5F0F\u3001\u4EC0\u4E48\u524D\u540E\u6846\u67B6\u672C\u8EAB\u5173\u7CFB\u4E0D\u5927\u3002</p><blockquote><p><strong>\u524D\u7AEF\u5F88\u5BB9\u6613\uFF1Fxx\u524D\u7AEF\u6846\u67B6\u662F\u5B66\u4E60\u540E\u7AEF\u6846\u67B6\u7684\u601D\u60F3\u7684\uFF1F</strong></p></blockquote><p>\u4E1A\u754C\u6709\u4E2A\u7ECF\u5178\u7684\u524D\u7AEF\u81EA\u9ED1\u7684\u7B11\u8BDD\uFF0C\u8BF4\u4E00\u4E2A\u4E0D\u592A\u61C2\u6280\u672F\u7684\u9886\u5BFC\u95EE\u9879\u76EE\u7ECF\u7406\uFF0C\u524D\u7AEF\u662F\u5E72\u4EC0\u4E48\u7684\uFF0C\u4E3A\u4EC0\u4E48\u8981\u8BBE\u7F6E\u8FD9\u4E2A\u5C97\u4F4D\uFF0C\u4E3A\u4EC0\u4E48\u6709\u4E86\u7F8E\u5DE5\u8FD8\u8981\u6709\u524D\u7AEF\u3002 \u5B9E\u9645\u4E0A\u8FD9\u5DF2\u4E0D\u662F\u4E2A\u7B11\u8BDD\uFF0C\u56E0\u4E3A\u6211\u9047\u5230\u8FC7\u592A\u591A\u88AB\u8F7B\u89C6\u7684\u4E8B\u3002 \u8FD8\u9047\u5230\u8FC7\uFF0C\u9879\u76EE\u5728\u5F00\u59CB\u65F6\u4E0D\u592A\u60F3\u5728\u524D\u7AEF\u6295\u5165\u8D44\u6E90\uFF0C\u5230\u540E\u671F\u5B9E\u5728\u6709\u524D\u7AEF\u95EE\u9898\u641E\u4E0D\u5B9A\u4E86\uFF0C\u5C31\u6765\u627E\uFF0C\u201C\u6709\u4E2A\u7B80\u5355\u7684\u524D\u7AEF\u95EE\u9898\uFF0C\u4F60\u5E2E\u5FD9\u770B\u770B\u201D\u2026\u2026\u524D\u7AEF\u4EBA\u5458\u96BE\u4EE5\u627F\u53D7\u4E4B\u75DB\uFF01 UI\u8BBE\u8BA1\u5E08\u597D\u6B79\u53EA\u662F\u88AB\u79F0\u547C\u6210\u7F8E\u5DE5\u561B\uFF0C\u524D\u7AEF\u8FDE\u5B58\u5728\u7684\u610F\u4E49\u90FD\u88AB\u8D28\u7591\u4E86\u3002</p><p>\u53C8\u6709\u6B21\u4E00\u4F4D\u540E\u7AEF\u5F00\u53D1\u627E\u5230\uFF0C\u8BA9\u5E2E\u5FD9\u5904\u7406\u4E2A\u6837\u5F0F\u7684 BUG\uFF0C\u662F\u76F4\u63A5\u5728\u4ED6\u7684\u7535\u8111\u4E0A\u4FEE\u6539\u7684\u3002\u4ED6\u7814\u7A76\u4E86\u5C0F\u534A\u5929\u5427\uFF0C\u624D\u627E\u6765\u7684\uFF0C\u7ED3\u679C\u6211\u534A\u5206\u949F\u5C31\u641E\u5B9A\u4E86\uFF0C\u7136\u540E\u4ED6\u5E76\u6CA1\u6709\u89C9\u5F97\u6211\u6280\u672F\u591A\u725B\u6216\u7ECF\u9A8C\u4E30\u5BCC\uFF0C\u800C\u662F\u54E6\u4E86\u4E0B\uFF0C\u8BF4\u539F\u6765\u8FD9\u6837\uFF0C\u4E0B\u6B21\u6211\u4E5F\u4F1A\u4E86\u3002 \u5F53\u65F6\u5185\u5FC3\u9887\u611F\u597D\u7B11\u3002\u4E0D\u662F\u56E0\u4E3A\u4ED6\u7684\u8BC4\u4EF7\u5BF9\u6211\u6709\u591A\u91CD\u8981\uFF0C\u800C\u662F\u8FD9\u4E0D\u662F\u201C\u77E5\u9053\u67D0\u4E2A\u5C5E\u6027\u201D\u5C31\u80FD\u89E3\u51B3\u7684\u95EE\u9898\uFF0C\u800C\u662F\u7406\u89E3\u4E00\u4E9B DOM \u5E03\u5C40\u7684\u6A21\u5F0F\u624D\u597D\u61C2\u7684\u4E8B\uFF0C\u5426\u5219\u4ED6\u4E0B\u6B21\u8FD8\u662F\u53EA\u4F1A\u590D\u5236\u7C98\u8D34\u3002</p><p>\u5BF9\u540E\u7AEF\u540C\u5B66\u5E76\u65E0\u6076\u610F\u4E0E\u4E0D\u656C\u3002 \u524D\u7AEF\u3001\u540E\u7AEF\u672F\u4E1A\u6709\u4E13\u653B\uFF0C\u5404\u6709\u5404\u7684\u4E0D\u5BB9\u6613\u3002 CSS \u5C31\u662F\u4E00\u4E2A\u4E2A\u7684\u5C5E\u6027\u8BBE\u7F6E\uFF0C\u6CA1\u592A\u591A\u7684\u903B\u8F91\uFF0C\u6240\u4EE5\u5165\u95E8\u5F88\u5BB9\u6613\uFF0C\u5F80\u5F80\u88AB\u201C\u7231\u5C55\u793A\u6280\u672F\u8D85\u7FA4\u201D\u7684\u7A0B\u5E8F\u5458\u4EEC\u6240\u4E0D\u5C51\uFF0C\u8BA4\u4E3A\u8FD9\u662F\u65E0\u6280\u672F\u542B\u91CF\u7684\u5DE5\u4F5C\u3002\u4F46\u8981\u5199\u4E00\u540E\u6F02\u4EAE\u7684\u7ED3\u6784\u548C\u6837\u5F0F\uFF0C\u771F\u4E0D\u662F\u5BB9\u6613\u7684\u4E8B\u3002\u5373\u4F7F\u80FD\u5199\u5F97\u5F88\u597D\uFF08\u4EE3\u7801\u7B80\u6D01\u3001\u53EF\u8BFB\u6027\u9AD8\u3001\u6613\u4E8E\u91CD\u6784\u91CD\u7528\u2026\u2026\uFF09\uFF0C\u5BA2\u6237\u77E5\u9053\u4E48\uFF1F\u9886\u5BFC\u4E70\u8D26\u4E48\uFF1F\u518D\u5389\u5BB3\u4E5F\u5C31\u88AB\u79F0\u547C\u4E3A\u201C\u9AD8\u7EA7\u9875\u9762\u4ED4\u201D\u3002\u7EFC\u5408\u800C\u8A00\u5C31\u662F\u4E2A\u5403\u529B\u4E0D\u8BA8\u597D\u7684\u5DE5\u4F5C\u3002 \u6211\u81EA\u5DF1\u4F5C\u4E3A\u524D\u7AEF\uFF0C\u90FD\u6E10\u6E10\u4E0D\u592A\u7231\u5199\u6837\u5F0F\u4E86\uFF0C\u66F4\u522B\u8BF4\u540E\u7AEF\u4E86\u3002\u6700\u597D\u662F\u6709\u73B0\u6210\u7684\u3001\u7F8E\u89C2\u7684 UI \u5E93\uFF0C\u5F00\u7BB1\u5373\u7528\u3002 \u4EC0\u4E48\uFF1FUI \u8BBE\u8BA1\u5E08\u5728\u5077\u5077\u641E\u4EC0\u4E48\u521B\u65B0\u8BBE\u8BA1\u548C\u5C4C\u70B8\u5929\u7684\u6837\u5F0F\uFF1F\u8D76\u7D27\u4E0A\u53BB\u4E00\u638C\u62CD\u6B7B\u3002</p><p>\u77E5\u9053\u4E00\u4E9B\u524D\u7AEF\u540E\u7AEF\u4FF1\u4F73\uFF0C\u751A\u81F3 UI \u90FD\u7CBE\u901A\u7684\u5927\u725B\uFF0C\u6839\u672C\u5C31\u4E0D\u4F1A\u53BB\u53D1\u8868xx\u884C\u4E1A/\u5C97\u4F4D\u56F0\u96BE\u6216\u662F\u5BB9\u6613\u7684\u3002\u4ED6\u4EEC\u6CA1\u7A7A\u3002</p>",10),a=[s];function c(p,i,d,_,n,m){return t(),o("div",null,a)}var f=e(r,[["render",c]]);export{S as __pageData,f as default};
