import{_ as e,o as t,c as r,a}from"./app.0e46eb75.js";const u=JSON.parse('{"title":"\u8BF4\u8BDD\u4E4B\u9053","description":"","frontmatter":{"updateTime":"2014/04/03","title":"\u8BF4\u8BDD\u4E4B\u9053"},"headers":[{"level":2,"title":"\u8BF4\u8BDD\u4E4B\u9053","slug":"\u8BF4\u8BDD\u4E4B\u9053","link":"#\u8BF4\u8BDD\u4E4B\u9053","children":[]},{"level":2,"title":"\u60AC\u7591\u5F0F\u8BF4\u8BDD\u7EC3\u4E60","slug":"\u60AC\u7591\u5F0F\u8BF4\u8BDD\u7EC3\u4E60","link":"#\u60AC\u7591\u5F0F\u8BF4\u8BDD\u7EC3\u4E60","children":[]}],"relativePath":"ESSAY/talkWithSuspense.md"}'),s={name:"ESSAY/talkWithSuspense.md"},n=a('<h2 id="\u8BF4\u8BDD\u4E4B\u9053" tabindex="-1">\u8BF4\u8BDD\u4E4B\u9053 <a class="header-anchor" href="#\u8BF4\u8BDD\u4E4B\u9053" aria-hidden="true">#</a></h2><p>\u8BB8\u591A\u4EBA\u90FD\u770B\u8FC7\u8521\u5EB7\u6C38\u4E3B\u6301\u7684\u8282\u76EE\u5427\uFF1F\u4ED6\u4E0D\u50CF\u4E00\u4E9B\u540C\u884C\u4E3B\u6301\u90A3\u6837\u6216\u80FD\u591A\u624D\u591A\u827A\uFF0C\u6216\u80FD\u8BF4\u5B66\u9017\u5531\uFF0C\u6216\u80FD\u63D2\u79D1\u6253\u8BE8\uFF0C\u6216\u80FD\u5999\u8BED\u8FDE\u73E0\uFF0C\u6216\u80FD\u6ED4\u6ED4\u4E0D\u7EDD\uFF0C\u8BA9\u6211\u4F69\u670D\u7684\u662F\u4ED6\u8C08\u5B9A\u7684\u63A7\u573A\u80FD\u529B\u3002<br> \u3000\u3000\u6280\u672F\u7814\u7A76\u51FA\u8EAB\u7684\u6211\u5728\u751F\u6D3B\u4E2D\u7684\u66F4\u591A\u65F6\u5019\uFF0C\u4E5F\u662F\u4E2A\u6C89\u9ED8\u5C11\u8A00\u4E4B\u4EBA\u3002\u7ED9\u81EA\u5DF1\u8D34\u91D1\uFF0C\u662F\u201C\u4F4E\u8C03\u201D\uFF1B\u8001\u5B9E\u5730\u8BF4\uFF0C\u662F\u201C\u4E0D\u5584\u8A00\u8F9E\u201D\u3002\u4F46\u6211\u660E\u767D\uFF0C\u4E00\u4E2A\u4EBA\uFF0C\u80FD\u8BF4\u4F1A\u9053\u662F\u591A\u4E48\u7684\u91CD\u8981\u3002\u5DE5\u4F5C\u6C47\u62A5\u3001\u4F1A\u8BAE\u3001\u9053\u6B49\u3001\u6F14\u8BB2\u3001\u63A8\u9500\uFF0C\u548C\u540C\u4E8B\u548C\u4E0A\u53F8\u6C9F\u901A\uFF0C\u548C\u4EB2\u670B\u548C\u597D\u53CB\u76F8\u5904\uFF0C\u8FD9\u4E9B\u90FD\u548C\u4E00\u4EF6\u4E8B\u6709\u5173\uFF1A\u4F60\u4F1A\u4E0D\u4F1A\u8BF4\u8BDD\uFF0C\u4F60\u80FD\u4E0D\u80FD\u5728\u548C\u522B\u4EBA\u6C9F\u901A\u7684\u8FC7\u7A0B\u4E2D\u4E86\u89E3\u5230\u5BF9\u65B9\u5FC3\u91CC\u7684\u60F3\u6CD5\u3002<br> \u3000\u3000\u5728\u56FE\u4E66\u9986\u770B\u5230\u300A<a href="https://book.douban.com/subject/5317075/" target="_blank" rel="noreferrer">\u8521\u5EB7\u6C38\u7684\u8BF4\u8BDD\u4E4B\u9053</a>\u300B\u4E00\u4E66\uFF0C\u5C31\u6BEB\u4E0D\u72B9\u8C6B\u5730\u501F\u56DE\u5BB6\u770B\u4E86\u3002\u7BC7\u5E45\u4E0D\u957F\uFF0C\u50CF\u6211\u8FD9\u6837\u89C6\u529B\u4E0D\u4F73\u7684\uFF0C\u53EA\u82B1\u4E86\u4E2A\u8981\u5E38\u5E38\u6392\u961F\u7684\u5468\u672B\uFF0C\u5C31\u628A\u6574\u672C\u4E66\u8BFB\u5B8C\u4E86\u3002\u6CA1\u6709\u592A\u591A\u5927\u9053\u7406\uFF0C\u800C\u662F\u4ECE\u5F88\u591A\u4E0D\u4E4F\u5473\u7684\u5B9E\u4E8B\u4E2D\u6559\u4F1A\u8BFB\u8005\u4E00\u4E9B\u91CD\u8981\u800C\u5B9E\u7528\u7684\u8981\u8BE3\u3002</p><p>\u4E3E\u4E9B\u4F8B\u5B50\u5427\u3002</p><p>\u7B2C\u4E00\u7AE0\u662F\u544A\u8BC9\u6211\u4EEC\uFF0C\u65E5\u5E38\u4E0E\u4EBA\u8BF4\u8BDD\u7684\u4E00\u4E2A\u91CD\u8981\u76EE\u7684\uFF0C\u5C31\u662F\u8981\u8BA8\u4EBA\u559C\u6B22\u3002\u5982\u679C\u8BF4\u8BDD\u53EA\u662F\u4E3A\u4E86\u8BA9\u522B\u4EBA\u5BB3\u6015\u6216\u8BA8\u538C\u81EA\u5DF1\u7684\uFF0C\u5C31\u4E0D\u7528\u770B\u8FD9\u672C\u4E66\u4E86\u3002<br> \u3000\u3000\u6709\u4E86\u89E3\u8FC7\u201C\u8BF4\u8BDD\u6280\u5DE7\u201D\u7684\u4EBA\u90FD\u77E5\u9053\u9002\u5F53\u7684\u8D5E\u7F8E\u4F1A\u8BA8\u4EBA\u559C\u6B22\u3002\u53EF\u662F\u600E\u4E48\u8D5E\u7F8E\u80FD\u4E0D\u9732\u75D5\u8FF9\uFF0C\u542C\u8D77\u6765\u4E0D\u7A7A\u6D1E\u4E0D\u865A\u4F2A\u5462\uFF1F\u5982\u679C\u4F60\u9047\u4E0A\u4E00\u4E2A\u8457\u540D\u7535\u89C6\u5267\u91CC\u7684\u8001\u592A\u5A46\u5973\u4E3B\u89D2\uFF0C\u8981\u600E\u4E48\u8D5E\u7F8E\u5979\u5462\uFF1F\u201C\u8F6C\u8FF0\u522B\u4EBA\u7684\u8D5E\u7F8E\u201D\u542C\u8D77\u6765\u4F1A\u53EF\u4FE1\u5F97\u591A\u3002<br> \u3000\u3000\u201C\u4E0A\u6B21\u4E00\u8D77\u6253\u7FBD\u6BDB\u7403\uFF0C\u6211\u7684\u670B\u53CB\u90FD\u8BF4\u4F60\u4EBA\u5F88\u597D\u76F8\u5904\uFF0C\u4EBA\u53C8\u6F02\u4EAE\u5462\u201D<br> \u3000\u3000\u201C\u6211\u5BB6\u9694\u58C1\u7684\u963F\u4F2F\u662F\u60A8\u7684\u7C89\u4E1D\uFF0C\u8BF4\u60A8\u662F\u91CC\u9762\u5973\u89D2\u8272\u4E2D\u6700\u6709\u6C14\u8D28\u7684\u201D<br> \u3000\u3000\u201C\u5218\u79D1\u957F\uFF0C\u4E0A\u6B21\u6211\u548C**\u5B66\u6821\u7684\u4F55\u4E3B\u4EFB\u4E00\u8D77\u5403\u996D\uFF0C\u4F55\u4E3B\u4EFB\u90FD\u8BF4\u4F60\u5F88\u61C2\u7F51\u7EDC\u6280\u672F\uFF0C\u4EBA\u53C8\u8C6A\u723D\u201D<br> \u3000\u3000\u2026\u2026<br> \u3000\u3000\u53EA\u8981\u4E0D\u662F\u6545\u610F\u627E\u76F8\u53CD\u7684\u60C5\u51B5\u53BB\u8BF4\uFF0C\u5373\u4E0D\u662F\u5B8C\u5168\u4E00\u6837\u7684\u4E5F\u6CA1\u5173\u7CFB\uFF0C\u6B63\u5E38\u4EBA\u90FD\u4E0D\u4F1A\u62C6\u7A7F\u7684\u3002\u6240\u4EE5\u4E0B\u6B21\u6362\u6211\u5938\u4F60\u4EEC\u65F6\uFF0C\u867D\u7136\u4E5F\u662F\u5B9E\u60C5\uFF0C\u4F46\u4E5F\u4E0D\u7528\u592A\u5F97\u610F\u54E6\uFF01</p><h2 id="\u60AC\u7591\u5F0F\u8BF4\u8BDD\u7EC3\u4E60" tabindex="-1">\u60AC\u7591\u5F0F\u8BF4\u8BDD\u7EC3\u4E60 <a class="header-anchor" href="#\u60AC\u7591\u5F0F\u8BF4\u8BDD\u7EC3\u4E60" aria-hidden="true">#</a></h2><p>\u7B2C\u5341\u516D\u7AE0\u662F\u7EC3\u4E60\u60AC\u7591\u5F0F\u8BF4\u8BDD\u3002<br> \u3000\u3000\u8BB0\u5F97\u5B66\u751F\u65F6\u4EE3\u4E00\u8D77\u4F4F\u5BBF\u820D\u65F6\uFF0C\u5C0F\u4F19\u4F34\u4EEC\u6709\u65F6\u7761\u4E0D\u7740\u89C9\u4F1A\u4E00\u8D77\u780D\u5927\u5C71\u8BB2\u6709\u8DA3\u7684\u4E8B\u3002\u6709\u7684\u4EBA\u4E00\u8BB2\u8D77\u6765\uFF0C\u522B\u4EBA\u90FD\u5F88\u7231\u542C\uFF0C\u50CF\u4E2A\u597D\u5947\u7684\u5B69\u5B50\u4E00\u76F4\u542C\uFF1B\u6709\u7684\u4EBA\u8BB2\u4E00\u534A\uFF0C\u522B\u4EBA\u5C31\u77E5\u9053\u7ED3\u679C\u4E86\uFF0C\u4E8E\u662F\u8D77\u54C4\u6362\u8BDD\u9898\u3002<br> \u3000\u3000\u4EBA\u4E4B\u5E38\u60C5\uFF0C\u90FD\u662F\u5FCD\u4E0D\u4F4F\u60F3\u77E5\u9053\u201C\u540E\u6765\u5462\u201D\uFF1F\u8FD9\u5C31\u662F\u4E3A\u4EC0\u4E48\u65B0\u95FB\u62A5\u9053\u90FD\u8981\u7528\u4E9B\u8BED\u4E0D\u60CA\u4EBA\u6B7B\u4E0D\u4F11\u7684\u6807\u9898\uFF0C\u4E3A\u4EC0\u4E48\u7535\u89C6\u5267\u603B\u8981\u5728\u5200\u780D\u4E0B\u53BB\u65F6\uFF0C\u6216\u8005\u5728\u610F\u60F3\u4E0D\u5230\u7684\u4EBA\u4ECE\u540E\u9762\u51FA\u73B0\u65F6\uFF0C\u63D2\u64AD\u5E7F\u544A\u6216\u8005\u7B49\u4E0B\u4E00\u96C6\u3002<br> \u3000\u3000\u8981\u7EC3\u4E60\u8FD9\u79CD\u60AC\u7591\u5F0F\u8BF4\u8BDD\u5176\u5B9E\u5F88\u65B9\u4FBF\uFF0C\u4EE5\u540E\u6362\u4F60\u5728\u90E8\u95E8\u4F1A\u8BAE\u4E0A\u8BF4\u8BDD\u65F6\uFF0C\u6BCF\u8BB2\u51E0\u53E5\uFF0C\u5230\u5173\u952E\u7684\u5730\u65B9\u7A0D\u505C\u4E00\u4E0B\uFF0C\u770B\u6709\u6CA1\u6709\u540C\u4E8B\u95EE\u4F60\u201C\u7136\u540E\u5462\u201D\uFF0C\u6216\u8005\u81F3\u5C11\u4E22\u4E0B\u6B63\u5728\u73A9\u7684\u624B\u673A\u62AC\u5934\u770B\u4F60\u4E00\u4E0B\u3002\u5982\u679C\u6709\uFF0C\u8BF4\u660E\u4F60\u8BF4\u8BDD\u7684\u65B9\u5F0F\u662F\u5438\u5F15\u4EBA\u7684\u3002\u5982\u679C\u4F60\u600E\u4E48\u505C\u987F\uFF0C\u5927\u5BB6\u8FD8\u662F\u5728\u7559\u610F\u81EA\u5DF1\u7684\u4E1C\u897F\uFF0C\u90A3\u5C31\u8BF4\u660E\u4F60\u8FD8\u5F97\u7EC3\u4E60\u4E86\uFF0C\u522B\u4EBA\u751A\u81F3\u90FD\u6CA1\u7559\u610F\u4F60\u505C\u987F\u8FC7\u4E86\u3002<br> \u3000\u3000\u6211\u6709\u4E2A\u4E00\u8D77\u94BB\u7814\u4E864\u5E74\u6280\u672F\u7684\u5927\u5B66\u540C\u5B66\uFF0C\u5728\u6211\u5DF2\u7ECF\u80FD\u517B\u6D3B\u81EA\u5DF1\u65F6\uFF0C\u4ED6\u5DE5\u4F5C\u8FD8\u6CA1\u7740\u843D\uFF0C\u53EF\u662F\u540E\u6765\u4ED6\u5C31\u5F00\u59CB\u7EC3\u4E60\u8BF4\u8BDD\u7684\u6280\u5DE7\uFF0C\u628A\u5927\u4E8B\u5C0F\u4E8B\u90FD\u60F3\u529E\u6CD5\u8BB2\u5F97\u66F4\u5438\u5F15\u4EBA\u3002\u7ED3\u679C\u4ED6\u73B0\u5728\u5DF2\u7ECF\u8F6C\u884C\u5230\u65B9\u6B63\u516C\u53F8\u5F53\u5927\u9879\u76EE\u9500\u552E\u7ECF\u7406\u4E86\u3002</p><p>\u4F60\u4EEC\u77E5\u9053\u4ED6\u8F6C\u884C\u540E\u6BCF\u4E2A\u6708\u6BD4\u6211\u591A\u8D5A\u591A\u5C11\u5417\uFF1F<br> \u3000\u3000\u4E5F\u8BB8\u4F60\u5F88\u60F3\u77E5\u9053\u7B54\u6848\uFF0C\u4F46\u8FD9\u4EF6\u4E8B\u6211\u8FD8\u662F\u8BF4\u5230\u8FD9\u91CC\u597D\u4E86\u3002</p>',7),i=[n];function o(l,_,c,d,p,h){return t(),r("div",null,i)}const S=e(s,[["render",o]]);export{u as __pageData,S as default};
