import{_ as e,c as t,o as a,a as o}from"./app.0f2eebca.js";const m=JSON.parse('{"title":"\u5F53\u5E78\u798F\u6765\u6572\u95E8","description":"","frontmatter":{"updateTime":"2012/03/03","title":"\u5F53\u5E78\u798F\u6765\u6572\u95E8"},"headers":[],"relativePath":"ESSAY/ThePursuitOfHappyness.md","lastUpdated":1660284577000}'),s={name:"ESSAY/ThePursuitOfHappyness.md"},r=o('<blockquote><p>They all looked so damn happy to me. Why couldn&#39;t I look like that? </p></blockquote><h1 id="\u300C\u5F53\u5E78\u798F\u6765\u6572\u95E8\u300D\u89C2\u540E" tabindex="-1">\u300C\u5F53\u5E78\u798F\u6765\u6572\u95E8\u300D\u89C2\u540E <a class="header-anchor" href="#\u300C\u5F53\u5E78\u798F\u6765\u6572\u95E8\u300D\u89C2\u540E" aria-hidden="true">#</a></h1><p>The Pursuit Of Happyness\uFF0C\u8FD9\u662F\u4E00\u90E8\u52B1\u5FD7\u7535\u5F71\uFF0C\u53D6\u6750\u4E8E\u771F\u5B9E\u6545\u4E8B\uFF0C\u4E3B\u89D2\u5C31\u662F\u5F53\u4ECA\u7F8E\u56FD\u9ED1\u4EBA\u6295\u8D44\u4E13\u5BB6 <a href="http://baike.baidu.com/view/1672720.htm" target="_blank" rel="noopener noreferrer">Chris Gardner</a>\u3002\u6545\u4E8B\u8BB2\u8FF0\u4E86\u4E00\u4E2A\u6FD2\u4E34\u7834\u4EA7\u3001\u8001\u5A46\u79BB\u5BB6\u7684\u843D\u9B44\u4E1A\u52A1\u5458\uFF0C\u5982\u4F55\u523B\u82E6\u8010\u52B3\u5730\u5C3D\u7740\u7236\u4EB2\u7684\u8D23\u4EFB\u53BB\u517B\u80B2\u3001\u6559\u5BFC\u5B69\u5B50\uFF0C\u594B\u53D1\u5411\u4E0A\u6210\u4E3A\u80A1\u5E02\u4EA4\u6613\u5458\uFF0C\u6700\u540E\u6210\u4E3A\u77E5\u540D\u7684\u91D1\u878D\u6295\u8D44\u5BB6\u3002 \u3000\u3000Chris Gardner \u7684\u806A\u660E\u777F\u667A\u3001\u4E50\u89C2\u5065\u8C08\uFF0C\u8BA9\u4EBA\u7FA1\u6155\u3002\u4F46\u66F4\u96BE\u5F97\u7684\u662F\uFF0C\u4ED6\u65E0\u8BBA\u5904\u5728\u591A\u6076\u52A3\u7684\u73AF\u5883\u4E2D\uFF0C\u751A\u81F3\u6709\u90A3\u4E48\u4E9B\u77AC\u95F4\u4E5F\u662F\u6B47\u65AF\u5E95\u91CC\u6376\u80F8\u987F\u8DB3\u4E86\uFF0C\u4F46\u4ECD\u4FDD\u6301\u7740\u4ED6\u7684\u4FE1\u5FF5\uFF0C\u5728\u5173\u952E\u7684\u65F6\u5019\u80FD\u4F5C\u51FA\u4E86\u6B63\u786E\u7684\u9009\u62E9\u3002\u59BB\u5B50\u79BB\u5F00\u4ED6\u5E76\u5632\u7B11\u4ED6\u4E3A\u4EC0\u4E48\u4E0D\u662F\u53BB\u5E94\u8058\u5B87\u822A\u5458\uFF1B\u623F\u4E1C\u4E5F\u8981\u8D76\u4ED6\u8D70\uFF1B\u201C\u670B\u53CB\u201D\u8FDE14\u7F8E\u5143\u4E5F\u4E0D\u80AF\u8FD8\u7ED9\u4ED6\u2026\u2026 \u3000\u3000\u6B64\u65F6\u4ED6\u662F\u8981\u9009\u62E9\u516D\u4E2A\u6708\u7684\u65E0\u85AA\u5B9E\u4E60\u673A\u4F1A\uFF0C\u8FD8\u662F\u9009\u62E9\u9000\u7F29\uFF0C\u5148\u627E\u4EFD\u80FD\u7CCA\u53E3\u7684\u5DE5\u4F5C\uFF1F \u3000\u3000\u770B\u5230\u8FD9\u91CC\u65F6\uFF0C\u6211\u6682\u505C\u4E86\u7535\u5F71\uFF0C\u9694\u4E86\u4E24\u5929\u624D\u7EE7\u7EED\u770B\uFF0C\u53BB\u60F3\u50CF\u5982\u679C\u662F\u6211\u9047\u5230\u8FD9\u60C5\u51B5\u4F1A\u600E\u4E48\u529E\u3002\u540E\u6765\u77E5\u9053\u7ED3\u679C\u65F6\uFF0C\u867D\u7136\u5728\u610F\u6599\u4E4B\u5185\uFF0C\u4F46\u8FD8\u662F\u76F8\u5F53\u60CA\u8BB6\uFF1A\u4ED6\u80CC\u6C34\u4E00\u6218\u5730\u9009\u62E9\u4E86\u516D\u4E2A\u6708\u7684\u65E0\u85AA\u5B9E\u4E60\u673A\u4F1A\uFF0C\u53BB\u548C\u6761\u4EF6\u6BD4\u81EA\u5DF1\u4F18\u8D8A\u5F97\u591A\u7684\u5176\u4F5919\u4EBA\u7ADE\u4E89\u552F\u4E00\u7684\u673A\u4F1A\uFF01</p><p>Chris \u5728\u7BEE\u7403\u573A\u6559\u80B2\u513F\u5B50\u7684\u8BDD\u8BA9\u6211\u5370\u8C61\u6700\u4E3A\u6DF1\u523B\uFF1A</p><blockquote><p>You got a dream, you gotta protect it. People can&#39;t do something themselves, they wanna tell you you can&#39;t do it. \u5982\u679C\u4F60\u6709\u68A6\u60F3\u7684\u8BDD\uFF0C\u5C31\u8981\u53BB\u634D\u536B\u5B83\u3002\u90A3\u4E9B\u4E00\u4E8B\u65E0\u6210\u7684\u4EBA\u603B\u60F3\u544A\u8BC9\u4F60\u4F60\u4E5F\u6210\u4E0D\u4E86\u5927\u5668\u3002</p></blockquote><p>Chris \u8BF4\u7684\u6B63\u662F\u4E8B\u5B9E\u3002\u6211\u4EEC\u52AA\u529B\u5730\u505A\u67D0\u4EF6\u4E8B\u65F6\uFF0C\u9047\u5230\u7684\u963B\u788D\u5B9E\u5728\u592A\u591A\u4E86\u3002\u522B\u4EBA\u7684\u975E\u8BAE\uFF0C\u7ADE\u4E89\u8005\u7684\u6253\u51FB\uFF0C\u73AF\u5883\u548C\u80FD\u529B\u7684\u5236\u7EA6\u2026\u2026\u968F\u4FBF\u4E00\u6837\u90FD\u80FD\u5C06\u4E00\u4E2A\u672C\u6765\u96C4\u5FC3\u52C3\u52C3\u7684\u4EBA\u6253\u57AE\u3002</p><blockquote><p>\u4ED6\u4EEC\u5168\u90FD\u770B\u8D77\u6765\u8D85\u5E78\u798F\u7684\u6837\u5B50\u3002\u4E3A\u4EC0\u4E48\u6211\u4E0D\u80FD\uFF1F</p></blockquote><div class="tip custom-block"><p class="custom-block-title">\u7FA1\u6155</p><p>\u50CF\u521A\u624D\u7684\u7535\u5F71\u91CC\u8BF4\u7684\u6545\u4E8B\uFF0C\u7A77\u6210\u90A3\u6837\u4E86\uFF0C\u60F3\u6253\u4E2A\u7BEE\u7403\uFF0C\u8FD8\u662F\u5F88\u5BB9\u6613\u7684\uFF0C\u4ED6\u4EEC\u6253\u7BEE\u7403\u7684\u5730\u65B9\uFF0C\u573A\u5730\u4E00\u6D41\uFF0C\u4F46\u7A7A\u7A7A\u7684\u3002</p></div>',8),p=[r];function c(i,n,l,d,h,u){return a(),t("div",null,p)}var k=e(s,[["render",c]]);export{m as __pageData,k as default};
